import React, { useEffect, useState} from 'react';

import AddToDo from './AddToDo.jsx';
import { SERVER_URL } from '../constants.js'
import MyEdit from './MyEdit';
import Pagination from './Pagination';
import EditToDo from './EditToDo.jsx';
import MyDataGrid from './MyDataGrid';

function ToDolist() {
    const [todos, setTodos] = useState([]);
    const [openEdit, setOpenEdit] = useState(false);
    const [selectedRow, setSelectedRow] = useState({});
    const [openAdd, setOpenAdd] = useState(false);
    const [done, setDone] = useState(true);

    // Fetch todoos front api the first the app is rendered
    useEffect(() => {
        fetchToDos();
    }, []);       

    // Makes a GET request to the API
    const fetchToDos = () => {
    fetch(SERVER_URL + 'todos')
    .then(response => response.json())
    .then(data => setTodos(data._embedded.toDoList))
    .catch(err => console.error(err));    
    }

    // Calls the api with a DELETE request for a task
    const onDelClick = (url) => {
        if(window.confirm('Are you sure to delete?')) {
            fetch(url, {method: 'DELETE'})
            .then(response => {
                if(response.ok){
                    fetchToDos();
                }
                else {
                    alert('Somehting went wrong!');
                }
            })
            .catch( err => console.error(err));
        }
    }

    // Add a new todo
    const addTodo = (todo) => {
        fetch(SERVER_URL + 'todos',
        {
            method: 'POST',
            headers: { 'Content-Type':'application/json' },
            body: JSON.stringify(todo)
        })
        .then(response => {
            if(response.ok) {
                fetchToDos();
            }
            else{
                alert("Something went wrong!");
            }
        })
        .catch(err => console.error(err))
    }

    // Calls the api to update a todo
    const updateTodo = (todo, link) => {
        console.log(todo);
        fetch(link,
            {
                method: 'PUT',
                headers: { 'Content-Type':  'application/json' },
                body: JSON.stringify(todo)
            })
            .then(response => {
                if(response.ok) {
                    fetchToDos();
                }
                else {
                    alert('Something went wrong!');
                }
            })
            .catch(err => console.error(err))
    }

    const done_undone_Todo = (link) => {
        fetch(link,
            {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' }
            })
            .then(response => {
                if(response.ok){
                    fetchToDos();
                }
                else {
                    alert('Something went wrong!');
                }
            })
            .catch(err => console.error(err))
    }

    const handleEdit = (row) => {
        console.log(row);
        setOpenEdit(true);
    }

    const handleDone = (link) => {
        setDone(!done);
        console.log("entre a handleDone y done es " + done );
        if(done){
            fetch(link + '/done',
                {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' }
                })
                .then(response => {
                    if(response.ok){
                        fetchToDos();
                    }
                    else {
                        alert('Something went wrong!');
                    }
                })
                .catch(err => console.error(err))
        }
        else {
            fetch(link + '/undone',
                {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' }
                })
                .then(response => {
                    if(response.ok){
                        fetchToDos();
                    }
                    else {
                        alert('Something went wrong!');
                    }
                })
                .catch(err => console.error(err))
        }

    }

    return(
        // <React.Fragment>
        //     <AddToDo addTodo={addTodo} />
        //     <div style={{ height: 500, width: '100%' }}>
        //         {/* <DataGrid
        //             rows={todos}
        //             columns={columns}
        //             getRowId={row => row._links.self.href}
        //             disableSelectionOnClick/> */}
        //         <MyDataGrid 
        //             rows={todos}/>
        //         <Snackbar
        //             open={open}
        //             autoHideDuration={2000}
        //             onClose={() => setOpen(false)}
        //             message="Task deleted"
        //         />
        //     </div>
        // </React.Fragment>

        <React.Fragment>
            <button className="primaryBtn" onClick={() => {
                setOpenAdd(true);
            }}> Add Task</button>
            <div className='MyDataGrid'>
            <table>
                <tr>
                    <th>    </th>
                    <th>Task</th>
                    <th>Priority</th>
                    <th>Due Date</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                    {todos.map((val, key) => {
                        return (
                            <tr id={val.id} key={val.id}>
                                <td> 
                                    <input type="checkbox"
                                        name="done"
                                        value="true"
                                        onClick={() => handleDone(val._links.self.href)} /> 
                                </td>
                                <td>{val.text}</td>
                                <td>{val.priority} </td>
                                <td>{val.due_date}</td>
                                <td>
                                    <button className="primaryBtn"  onClick={() => {
                                                    setOpenEdit(true);
                                                    setSelectedRow(val);
                                    }}
                                    >Edit</button>
                                </td>
                                <td>
                                    <button className="primaryBtn" onClick={ () => onDelClick(val._links.self.href) } > 
                                        Delete 
                                    </button>
                                </td>
                            </tr>
                        )
                    })}
            </table>
            <br />
            <Pagination/>
            {openAdd && <AddToDo addTodo={addTodo}
                                 setOpen={setOpenAdd} />}
            {openEdit && <MyEdit data={ selectedRow } 
                                setOpen={setOpenEdit}
                                updateTodo={updateTodo} />}
        </div>
        </React.Fragment>
    );
}
export default ToDolist;
