import React from 'react';
import '../App.css'; 
import './MyDataGrid.css'

function MyDataGrid(props) {

    const editTodo = () => {

    }

    const fetchToDos = () => {

    }

    const onDelClick = (url) => {
        if(window.confirm('Are you sure to delete?')) {
            fetch(url, {method: 'DELETE'})
            .then(response => {
                if(response.ok){
                    fetchToDos();
                }
                else {
                    alert('Somehting went wrong!');
                }
            })
            .catch( err => console.error(err));
        }
    }

    return(
        <div className='MyDataGrid'>
            <table>
                <tr>
                    <th>    </th>
                    <th>Task</th>
                    <th>Priority</th>
                    <th>Due Date</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                    {props.rows.map((val, key) => {
                        return (
                            <>
                                <tr key={key}>
                                    <td> {key}   </td>
                                    <td>{val.text}</td>
                                    <td>{val.priority} </td>
                                    <td>{val.due_date}</td>
                                    <td>
                                        <button onClick={editTodo}>Edit</button>
                                    </td>
                                    <td>
                                        <button onClick={() => onDelClick(val._links.self.href)}></button>
                                    </td>
                                </tr>
                                <br />
                            </>
                        )
                    })}
            </table>
            <br />
            <div>Aqui van los bootones de la tabla</div>
        </div>
    );
}

export default MyDataGrid;
