    //         headerName: '',
    //         sortable: false,
    //         filterable: false,
    //         renderCell: row =>
    //           <button
    //              onClick={() => onDelClick(row.id)}>Delete
    //           </button>
    //     }
    // ];

    useEffect(() => {
        fetchToDos();
    }, []);       

    const fetchToDos = () => {
    fetch(SERVER_URL + 'todos')
    .then(response => response.json())
    .then(data => setTodos(data._embedded.toDoList))
    .catch(err => console.error(err));    
    }

    const onDelClick = (url) => {
        if(window.confirm('Are you sure to delete?')) {
            fetch(url, {method: 'DELETE'})
            .then(response => {
                if(response.ok){
                    fetchToDos();
                }
                else {
                    alert('Somehting went wrong!');
                }
            })
            .catch( err => console.error(err));
        }
    }

    // Add a new todo
    const addTodo = (todo) => {
        fetch(SERVER_URL + 'todos',
        {
            method: 'POST',
            headers: { 'Content-Type':'application/json' },
            body: JSON.stringify(todo)
        })
        .then(response => {
            if(response.ok) {
                fetchToDos();
            }
            else{
                alert("Something went wrong!");
            }
        })
        .catch(err => console.error(err))
    }

    const updateTodo = (todo, link) => {
        console.log(todo);
        fetch(link,
            {
                method: 'PUT',
                headers: { 'Content-Type':  'application/json' },
                body: JSON.stringify(todo)
            })
            .then(response => {
                if(response.ok) {
                    fetchToDos();
                }
                else {
                    alert('Something went wrong!');
                }
            })
            .catch(err => console.error(err))
    }

    const done_undone_Todo = (link) => {
        fetch(link,
            {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' }
            })
            .then(response => {
                if(response.ok){
                    fetchToDos();
                }
                else {
                    alert('Something went wrong!');
                }
            })
            .catch(err => console.error(err))
    }

    const handleEdit = (row) => {
        console.log(row);
        setOpenEdit(true);
    }

    const handleDone = (link) => {
        setDone(!done);
        // if(done){
        //     fetch(link + '/done',
        //         {
        //             method: 'POST',
        //             headers: { 'Content-Type': 'application/json' }
        //         })
