        //                 alert('Something went wrong!');
        //             }
        //         })
        //         .catch(err => console.error(err))
        // }
        // else {
        //     fetch(link + '/undone',
        //         {
        //             method: 'POST',
        //             headers: { 'Content-Type': 'application/json' }
        //         })
        //         .then(response => {
        //             if(response.ok){
        //                 fetchToDos();
        //             }
        //             else {
        //                 alert('Something went wrong!');
        //             }
        //         })
        //         .catch(err => console.error(err))
        // }

    }

    return(
        // <React.Fragment>
        //     <AddToDo addTodo={addTodo} />
        //     <div style={{ height: 500, width: '100%' }}>
        //         {/* <DataGrid
        //             rows={todos}
        //             columns={columns}
        //             getRowId={row => row._links.self.href}
        //             disableSelectionOnClick/> */}
        //         <MyDataGrid 
        //             rows={todos}/>
        //         <Snackbar
        //             open={open}
        //             autoHideDuration={2000}
        //             onClose={() => setOpen(false)}
        //             message="Task deleted"
        //         />
        //     </div>
        // </React.Fragment>

        <React.Fragment>
            <button className="primaryBtn" onClick={() => {
                setOpenAdd(true);
            }}> Add Task</button>
            <div className='MyDataGrid'>
            <table>
                <tr>
                    <th>    </th>
                    <th>Task</th>
                    <th>Priority</th>
                    <th>Due Date</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                    {todos.map((val, key) => {
                        return (
                            <tr id={val.id} key={val.id}>
                                <td> 
                                    <input type="checkbox"
                                        name="done"
                                        value="true"
                                        onClick={() => handleDone(val._links.self.href)} /> 
                                </td>
                                <td>{val.text}</td>
                                <td>{val.priority} </td>
                                <td>{val.due_date}</td>
                                <td>
                                    <button className="primaryBtn"  onClick={() => {
                                                    setOpenEdit(true);
                                                    setSelectedRow(val);
                                    }}
                                    >Edit</button>
                                </td>
                                <td>
                                    <button className="primaryBtn" onClick={ () => onDelClick(val._links.self.href) } > 
                                        Delete 
                                    </button>
                                </td>
                            </tr>
                        )
                    })}
            </table>
            <br />
            <Pagination/>
            {openAdd && <AddToDo addTodo={addTodo}
                                 setOpen={setOpenAdd} />}
            {openEdit && <MyEdit data={ selectedRow } 
                                setOpen={setOpenEdit}
                                updateTodo={updateTodo} />}
        </div>
        </React.Fragment>
    );
}
export default ToDolist;
