import React, { useState } from 'react';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';

function EditToDo(props) {
  const [open, setOpen] = useState(false);
  const [todo, setTodo] = useState({
    text: '', priority: '', due_date: ''  
  });

  // Open the modal form
  const handleClickOpen = () => {
    setTodo({
        text: props.data.row.text,
        priority: props.data.row.priority,
        due_date: props.data.row.due_date
    })
    setOpen(true);
  };

  // Close the modal form
  const handleClose = () => {
    setOpen(false);
  };

  const handleChange = (event) => {
    setTodo({...todo,
      [event.target.name]: event.target.value});
  }
  // Update car and close modal form
  const handleSave = () => {
        props.updateTodo(todo, props.data.id);
        handleClose();
  }

  return(
    <div>
        <button onClick={handleClickOpen}>Edit</button>
        <Dialog open={open} onClose={handleClose}>
            <DialogTitle>Edit task</DialogTitle>
            <DialogContent>
                <input placeholder='Text' name='text'
                value={todo.text} onChange={handleChange} /><br/>
                <input placeholder='Priority' name='priority'
                value={todo.priority} onChange={handleChange} /><br/>
                <input placeholder='Due date' name='due_date'
                value={todo.due_date} onChange={handleChange} /><br/>
            </DialogContent>
            <DialogActions>
                <button onClick={handleClose}>Cancel</button>
                <button onClick={handleSave}>Save</button>
            </DialogActions>
        </Dialog>
    </div>
  );
}
export default EditToDo;
