import React, { useEffect, useState} from 'react';
import { DataGrid } from '@mui/x-data-grid';
import { Snackbar } from '@mui/material';

import AddToDo from './AddToDo.js';
import { SERVER_URL } from '../constants.js'
import EditToDo from './EditToDo';
import DoneToDo from './DoneToDo.js';
import MyDataGrid from './MyDataGrid.js';
import MyEdit from './MyEdit.js';
import Pagination from './Pagination.js';

function ToDolist() {
    const [open, setOpen] = useState(false);
    const [todos, setTodos] = useState([]);
    const [openEdit, setOpenEdit] = useState(false);
    //const [editRow setEditRow] = useState('');
    const [editRow, setEditRow] = useState('');

    // const columns = [
    //     {
    //         field: 'done',
    //         headerName: '',
    //         sortable: false,
    //         filterable: false,
    //         renderCell: row =>
    //             <DoneToDo
    //                 id={row.id}
    //                 doneTodo={done_undone_Todo} />
    //     },
    //     {field: 'text', headerName: 'Text', width: 200},
    //     {field: 'priority', headerName: 'Priority', width: 200},
    //     {field: 'due_date', headerName: 'Due Date', width: 200},
    //     {
    //         field: '_links.todos.href',
    //         headerName: '',
    //         sortable: false,
    //         filterable: false,
    //         renderCell: row =>
    //             <EditToDo
    //                 data={row}
    //                 updateTodo={updateTodo} />
    //     },
    //     {
    //         field: '_links.self.href',
    //         headerName: '',
    //         sortable: false,
    //         filterable: false,
    //         renderCell: row =>
    //           <button
    //              onClick={() => onDelClick(row.id)}>Delete
    //           </button>
    //     }
    // ];

    useEffect(() => {
        fetchToDos();
    }, []);       

    const fetchToDos = () => {
    fetch(SERVER_URL + 'todos')
    .then(response => response.json())
    .then(data => setTodos(data._embedded.toDoList))
    .catch(err => console.error(err));    
    }

    const onDelClick = (url) => {
    //     if (window.confirm("Are you sure to delete?")) {
    //     fetch(url,  {method:  'DELETE'})
    //     .then(response => {
    //         if (response.ok) { 
    //             fetchToDos();
    //             setOpen(true);
    //         }
    //         else {
    //             alert('Something went wrong!');
    //         }
    //     })    
    //     .catch(err => console.error(err))
    //   }
        if(window.confirm('Are you sure to delete?')) {
            fetch(url, {method: 'DELETE'})
            .then(response => {
                if(response.ok){
                    fetchToDos();
                }
                else {
                    alert('Somehting went wrong!');
                }
            })
            .catch( err => console.error(err));
        }
    }

    // Add a new todo
    const addTodo = (todo) => {
        fetch(SERVER_URL + 'todos',
        {
            method: 'POST',
            headers: { 'Content-Type':'application/json' },
            body: JSON.stringify(todo)
        })
        .then(response => {
            if(response.ok) {
                fetchToDos();
            }
            else{
                alert("Something went wrong!");
            }
        })
        .catch(err => console.error(err))
    }

    const editTodo = () => {

    }

    const updateTodo = (todo, link) => {
        fetch(link,
            {
                method: 'PUT',
                headers: { 'Content-Type':  'application/json' },
                body: JSON.stringify(todo)
            })
            .then(response => {
                if(response.ok) {
                    fetchToDos();
                }
                else {
                    alert('Something went wrong!');
                }
            })
            .catch(err => console.error(err))
    }

    const done_undone_Todo = (link) => {
        fetch(link,
            {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' }
            })
            .then(response => {
                if(response.ok){
                    fetchToDos();
                }
                else {
                    alert('Something went wrong!');
                }
            })
            .catch(err => console.error(err))
    }

    const handleEdit = (event) => {
        // console.log(event.parentElement.parentElement.innerText);
        setEditRow(event.parentElement.index);
        setOpenEdit(true);
    }

    return(
        // <React.Fragment>
        //     <AddToDo addTodo={addTodo} />
        //     <div style={{ height: 500, width: '100%' }}>
        //         {/* <DataGrid
        //             rows={todos}
        //             columns={columns}
        //             getRowId={row => row._links.self.href}
        //             disableSelectionOnClick/> */}
        //         <MyDataGrid 
        //             rows={todos}/>
        //         <Snackbar
        //             open={open}
        //             autoHideDuration={2000}
        //             onClose={() => setOpen(false)}
        //             message="Task deleted"
        //         />
        //     </div>
        // </React.Fragment>

        <React.Fragment>
            <div className='MyDataGrid'>
            <table>
                <tr>
                    <th>    </th>
                    <th>Task</th>
                    <th>Priority</th>
                    <th>Due Date</th>
                    <th>Edit</th>
                    <th>Delete</th>
                </tr>
                    {todos.map((val, key) => {
                        return (
                            <tr id={val.id} key={val.id}>
                                <td> {key}   </td>
                                <td>{val.text}</td>
                                <td>{val.priority} </td>
                                <td>{val.due_date}</td>
                                <td>
                                    <button className="primaryBtn"  onClick={handleEdit}>Edit</button>
                                    {openEdit && <MyEdit data={ editRow } 
                                                         setOpen={setOpenEdit}
                                                         updateTodo={updateTodo} />}
                                </td>
                                <td>
                                    <button className="primaryBtn" onClick={ () => onDelClick(val._links.self.href) } > 
                                        Delete 
                                    </button>
                                </td>
                            </tr>
                        )
                    })}
            </table>
            <br />
            <Pagination/>
        </div>
        </React.Fragment>
    );
}
export default ToDolist;
