import React, { useState} from 'react';
import "./Modal.css";
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';

function AddToDo({addTodo, setOpen}) {
    const [todo, setTodo] = useState({
        text: '',
        priority: '',
        due_date: ''
    });

    // Close the modal form
    const handleClose = () => {
        setOpen(false);
    }

    const handleChange = (event) => {
        setTodo({...todo, [event.target.name]:
            event.target.value});
    }

    // Save car and close modal form
    const handleSave = () => {
        addTodo(todo);
        handleClose();
    }

    return(
        // <div>
        //     <button onClick={handleClickOpen}>New Task</button>
        //     <Dialog open={open} onClose={handleClose}>
        //         <DialogTitle>New Task</DialogTitle>
        //         <DialogContent>
        //             <input placeholder="Text" name="text"
        //                 valie={todo.text} onChange={handleChange}/><br/>
        //             <input placeholder="Priority" name="priority"
        //                 valie={todo.priority} onChange={handleChange}/><br/>
        //                 <input placeholder="Due date" name="due_date"
        //                 valie={todo.due_date} onChange={handleChange}/><br/>
        //         </DialogContent>
        //         <DialogActions>
        //             <button onClick={handleClose}>Cancel</button>
        //             <button onClick={handleSave}>Save</button>
        //         </DialogActions>
        //     </Dialog>
        // </div>
        <React.Fragment>
            <div className="darkBG" onClick={() => setOpen(false)} />
                <div className="centered">
                    <div className="modal">
                        <div className="modalHeader">
                            <h5 className="heading">Add task</h5>
                        </div>
                        <div className="modalContent">
                            <input placeholder='Text' name='text' 
                                value={todo.text} onChange={handleChange} /><br/>
                            <input placeholder='Priority' name='priority'
                                value={todo.priority} onChange={handleChange} /><br/>
                            <input placeholder='Due date' name='due_date'
                                value={todo.due_date} onChange={handleChange} /><br/>
                        </div>
                        <div className="modalActions">
                            <div className="actionsContainer">
                                <button className="deleteBtn" onClick={handleSave}>
                                    Save
                                </button>
                                <button className="cancelBtn"
                                    onClick={handleClose}>    
                                    Cancel
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
        </React.Fragment>

    );
}
export default AddToDo;
