import React, { useState} from 'react';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';

function AddToDo(props) {
    const [open, setOpen] = useState(false);
    const [todo, setTodo] = useState({
        text: '',
        priority: '',
        due_date: ''
    });

    // Open the modal form
    const handleClickOpen = () => {
        setOpen(true);
    };

    // Close the modal form
    const handleClose = () => {
        setOpen(false);
    };

    const handleChange = (event) => {
        setTodo({...todo, [event.target.name]:
            event.target.value});
    }

    // Save car and close modal form
    const handleSave = () => {
        props.addTodo(todo);
        handleClose();
    }

    return(
        <div>
            <button onClick={handleClickOpen}>New Task</button>
            <Dialog open={open} onClose={handleClose}>
                <DialogTitle>New Task</DialogTitle>
                <DialogContent>
                    <input placeholder="Text" name="text"
                        valie={todo.text} onChange={handleChange}/><br/>
                    <input placeholder="Priority" name="priority"
                        valie={todo.priority} onChange={handleChange}/><br/>
                        <input placeholder="Due date" name="due_date"
                        valie={todo.due_date} onChange={handleChange}/><br/>
                </DialogContent>
                <DialogActions>
                    <button onClick={handleClose}>Cancel</button>
                    <button onClick={handleSave}>Save</button>
                </DialogActions>
            </Dialog>
        </div>
    );
}
export default AddToDo;
